"use strict";

const webpack = require("webpack");
const path = require("path");

class Config {
    constructor() {
        this.mHost = "0.0.0.0";
        this.mPackage = "package";
        this.mDist = "public";
    }

    package(name) {
        this.mPackage = name;
        return this;
    }

    getEntry() {
        return {
            app: path.join(process.cwd(), "src/app.ts")
        };
    }

    getOutput() {
        return {
            path: path.join(process.cwd(), this.mDist),
            filename: "[name].js"
        };
    }

    getDevServer() {
        return {
            host: this.mHost,
            contentBase: path.join(process.cwd(), this.mDist),
            hot: true,
            compress: true,
            inline: true,
            stats: {
                colors: true
            },
            watchOptions: {
                ignored: /node_modules/
            }
        };
    }

    getRules() {
        return [
            {
                test: /\.tsx?$/,
                use: "ts-loader",
                exclude: /node_modules/
            }
        ];
    }

    getModules() {
        return {
            rules: this.getRules()
        };
    }

    getPlugins() {
        return [
            new webpack.NamedModulesPlugin(),
            new webpack.IgnorePlugin(/^\.\/locale$/, /moment$/)
        ];
    }

    getResolvers() {
        return {
            extensions: [".tsx", ".ts", ".js"]
        };
    }

    build() {
        return {
            entry: this.getEntry(),
            devServer: this.getDevServer(),
            mode: "development",
            output: this.getOutput(),
            resolve: this.getResolvers(),
            module: this.getModules(),
            plugins: this.getPlugins()
        };
    }
}

module.exports = Config;
